.btn-mz {
  border: none;
  padding: 5px 20px;
  font-size: 16px;
  cursor: pointer;
  transition: all 0.3s;
  display: inline-block;
  font-family: "Comfortaa", cursive;
  font-weight: 700;
  text-align: center;
  border: 2px solid transparent;
  min-width: 120px;
}
.btn-mz::first-letter {
  text-transform: uppercase;
}
.btn-mz.btn--full {
  width: 100%;
  text-align: center;
}
.btn-mz.is--disabled {
  cursor: not-allowed;
  pointer-events: none;
}
.btn-mz.is--disabled span {
  opacity: 0.52;
}
.btn-mz:focus {
  outline: none;
}
.btn-mz:hover {
  color: #fff;
  text-decoration: none;
}

.btn--group .btn-mz:nth-of-type(2) {
  margin-left: 20px;
}
.btn--group .btn-mz--third {
  margin-left: 20px;
}

.btn--brand-color {
  --color: #36CCA5;
}

.btn--info-color {
  --color: #62B4F9;
}

.btn--product-color {
  --color: #FF8800;
}

.btn--details-color {
  --color: #FF5252;
}

.btn--business-color {
  --color: #B604B6;
}

.btn--userinput-color {
  --color: #63B4F9;
}

.btn--radius {
  border-radius: 31px;
}

.btn--outline {
  color: var(--color);
  border-color: var(--color);
  background-color: transparent;
}

.btn--filled {
  background-color: var(--color);
  color: #fff;
}

.btn--shadow {
  box-shadow: 0 0 6px var(--color);
  transition: all 0.5s;
}
.btn--shadow:hover {
  box-shadow: 0 0 27px 0 var(--color);
}

@media screen and (min-width: 320px) and (max-width: 1023px) {
  .btn--shadow {
    box-shadow: 0 0 27px 0 var(--color);
  }

  .btn--group .btn-mz--third {
    margin-left: 0px;
  }
}
