.first-letter-input {
  position: relative;
}
.first-letter-input input,
.first-letter-input textarea {
  background-color: transparent;
  width: 100%;
  padding-left: 0;
}
.first-letter-input input ~ label,
.first-letter-input textarea ~ label {
  position: absolute;
  left: 0;
  bottom: 0;
  right: 15px;
  font-size: 0;
  height: 0;
  width: 0;
  transition: all 0.5s;
  margin: 0;
}
.first-letter-input input ~ label:before,
.first-letter-input textarea ~ label:before {
  content: "";
  position: absolute;
  background-color: #707070;
  opacity: 0.6;
  left: 0;
  bottom: 0;
  width: 15px;
  height: 1px;
  transition: all 0.5s;
}
.first-letter-input input:hover ~ label:before, .first-letter-input input:focus ~ label:before,
.first-letter-input textarea:hover ~ label:before,
.first-letter-input textarea:focus ~ label:before {
  background-color: #757575;
  width: 150px;
}

