body {
  font-family: "Montserrat", sans-serif;
  overflow-x: hidden;
}
body.is--not-scrolable {
  overflow: hidden;
}

.section-padding {
  padding: 5rem 0;
}

header {
  position: fixed;
  top: 15px;
  left: 15px;
  padding: 10px 20px;
  border-radius: 50px;
  z-index: 99999999;
  min-height: 0px;
  transition: all 0.5s;
  background-color: #fff;
  box-shadow: 1px 1px 6px rgba(152, 152, 152, 0.4);
}
header .navigation {
  display: flex;
  align-items: center;
  flex-direction: column;
}
header .navigation--head {
  flex: 0 0 100%;
  display: flex;
  align-items: center;
  cursor: pointer;
}
header .navigation--bar {
  margin-right: 15px;
}
header .navigation--bar span {
  background-color: #36CCA5;
  width: 24px;
  height: 3px;
  border-radius: 5px;
  display: block;
  margin-bottom: 3px;
}
header .navigation--bar span:last-child {
  margin-bottom: 0px;
}
header .navigation--logo {
  max-width: 205px;
}
header .navigation--logo svg path {
  fill: #36CCA5;
}
header.is--open {
  border-radius: 10px;
  background-color: #fff;
  box-shadow: 1px 1px 6px rgba(152, 152, 152, 0.4);
}
header.is--sticked {
  background-color: #fff;
  box-shadow: 1px 1px 6px rgba(152, 152, 152, 0.4);
}
header.is--sticked .menu-container {
  max-width: 25px;
}
header.is--sticked .menu-container svg path {
  fill: #686868;
}
header.is--sticked .name-container path {
  fill: #686868;
}

.navigation--items {
  flex: 0 0 100%;
  display: none;
  padding-top: 40px;
  align-self: flex-start;
}
.navigation--items ul {
  list-style: none;
  padding: 0;
}
.navigation--items ul li {
  margin-bottom: 20px;
}
.navigation--items ul li a {
  color: #757575;
  font-size: 21px;
}

[data-first-layout=true] {
  margin-top: 3rem;
}

.description {
  font-size: 16px;
}
@media screen and (min-width: 768px) {
  .description {
    font-size: calc(16px + 4 * (100vw - 768px) / 598);
  }
}
@media screen and (min-width: 1366px) {
  .description {
    font-size: 20px;
  }
}

featured-tools {
  padding: 3rem 0;
  display: block;
}
featured-tools .heading {
  font-size: 20px;
  font-family: "Comfortaa", cursive;
  font-weight: 700;
}
@media screen and (min-width: 768px) {
  featured-tools .heading {
    font-size: calc(20px + 15 * (100vw - 768px) / 1151);
  }
}
@media screen and (min-width: 1919px) {
  featured-tools .heading {
    font-size: 35px;
  }
}
featured-tools .featured-tools-list {
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  margin-top: 3rem;
}
featured-tools .featured-tools-list .individual-tool {
  flex: 0 0 20%;
  text-align: center;
}
featured-tools .featured-tools-list .description {
  font-family: "Comfortaa", cursive;
  font-weight: 700;
}

.our-clients .client--intro {
  text-align: center;
  flex: 0 0 20%;
}
.our-clients .client--intro:nth-of-type(even) {
  margin-top: 5rem;
}
.our-clients .client--image {
  background-color: #fdfdfd;
  height: 120px;
  width: 120px;
  padding: 15px;
  display: flex;
  justify-content: center;
  align-items: center;
  margin: 0 auto;
  border-radius: 50%;
  box-shadow: 1px 0 2px 5px rgba(103, 58, 183, 0.33);
  transition: all 0.5s;
  margin-bottom: 25px;
  overflow: hidden;
}
.our-clients .client--image:hover {
  box-shadow: 1px 0 2px 5px #673ab7;
}
.our-clients .client--image:hover img {
  filter: grayscale(0%);
  opacity: 1;
}
.our-clients .client--image img {
  filter: grayscale(100%);
  opacity: 0.7;
  transition: all 0.5s;
  max-height: 100%;
}
.our-clients .client--name {
  font-size: 15px;
  font-family: "Comfortaa", cursive;
}
.our-clients .clients--more {
  font-weight: 800;
  font-size: 25px;
  margin-bottom: 0;
}

.stats-list {
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
}
.stats-list .individual-stat {
  flex: 0 0 20%;
  text-align: center;
}
.stats-list .stat-figures {
  position: relative;
  display: flex;
  align-items: center;
  justify-content: center;
}
.stats-list .stat-figures p {
  position: absolute;
  top: 50%;
  margin: 0;
  font-size: 20px;
  transform: translate(0%, -50%);
  transition: all 0.5s;
  font-weight: 700;
}
.stats-list .stat-figures .stat-bg {
  transition: all 0.5s;
}
.stats-list .description {
  font-size: 15px;
  font-family: Comfortaa;
}

.individual-stat:hover .stat-figures p {
  text-shadow: 1px 1px 2px rgba(102, 102, 102, 0.6);
}
.individual-stat:nth-of-type(odd):hover .stat-figures .stat-bg {
  animation: anticlockwise-rotation 3s linear infinite;
}
.individual-stat:nth-of-type(even):hover .stat-figures .stat-bg {
  animation: clockwise-rotation 3s linear infinite;
}

@keyframes clockwise-rotation {
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(-1000deg);
  }
}
@keyframes anticlockwise-rotation {
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(1000deg);
  }
}
testimonials {
