    // word-break: break-all;
    &::before {
      position: absolute;
      top: -40px;
      left: -5px;
      color: #fff;
      content: url("../images/left-quote.png");
    }
    &::after {
      position: absolute;
      bottom: -40px;
      right: -5px;
      color: #fff;
      content: url("../images/right-quote.png");
    }
  }

  .testimonial--author {
    @include fluid-type(font-size, 768px, 1919px, 14px, 19px);
