  .testimonial-carousel {
    margin-left:-15px;
    margin-right:-15px;
    .testimonial--text {
      opacity: 0.2;
      &::before {
        top: -40px;
        left: -45px;
        transform: scale(0.5);
      }
      &::after {
        bottom: -40px;
        right: -45px;
        transform: scale(0.5);
      }
    }
  }

  .slick-slide {
    .testimonial--text,
    .testimonial--author {
      opacity: 0.2;
    }

    &.slick-active {
      .testimonial--text,
      .testimonial--author {
        opacity: 1;
      }
    }
  }
}

