body {
  font-family: "Montserrat", sans-serif;
  overflow-x: hidden;
}

body.is--not-scrolable {
  overflow: hidden;
}

.section-padding {
  padding: 5rem 0;
}

header {
  position: fixed;
  top: 15px;
  left: 15px;
  padding: 10px 20px;
  border-radius: 50px;
  z-index: 99999999;
  min-height: 0px;
  -webkit-transition: all 0.5s;
  transition: all 0.5s;
  background-color: #fff;
  -webkit-box-shadow: 1px 1px 6px rgba(152, 152, 152, 0.4);
          box-shadow: 1px 1px 6px rgba(152, 152, 152, 0.4);
}

header .navigation {
  display: -webkit-box;
  display: -ms-flexbox;
  display: flex;
  -webkit-box-align: center;
      -ms-flex-align: center;
          align-items: center;
  -webkit-box-orient: vertical;
  -webkit-box-direction: normal;
      -ms-flex-direction: column;
          flex-direction: column;
}

header .navigation--head {
  -webkit-box-flex: 0;
      -ms-flex: 0 0 100%;
          flex: 0 0 100%;
  display: -webkit-box;
  display: -ms-flexbox;
  display: flex;
  -webkit-box-align: center;
      -ms-flex-align: center;
          align-items: center;
  cursor: pointer;
}

header .navigation--bar {
  margin-right: 15px;
}

header .navigation--bar span {
  background-color: #36CCA5;
  width: 24px;
  height: 3px;
  border-radius: 5px;
  display: block;
  margin-bottom: 3px;
}

header .navigation--bar span:last-child {
  margin-bottom: 0px;
}

header .navigation--logo {
  max-width: 205px;
}

header .navigation--logo svg path {
  fill: #36CCA5;
}

header.is--open {
  border-radius: 10px;
  background-color: #fff;
  -webkit-box-shadow: 1px 1px 6px rgba(152, 152, 152, 0.4);
          box-shadow: 1px 1px 6px rgba(152, 152, 152, 0.4);
}

header.is--sticked {
  background-color: #fff;
  -webkit-box-shadow: 1px 1px 6px rgba(152, 152, 152, 0.4);
          box-shadow: 1px 1px 6px rgba(152, 152, 152, 0.4);
}

header.is--sticked .menu-container {
  max-width: 25px;
}

header.is--sticked .menu-container svg path {
  fill: #686868;
}

header.is--sticked .name-container path {
  fill: #686868;
}

.navigation--items {
  -webkit-box-flex: 0;
      -ms-flex: 0 0 100%;
          flex: 0 0 100%;
  display: none;
  padding-top: 40px;
  -ms-flex-item-align: start;
      align-self: flex-start;
}

.navigation--items ul {
  list-style: none;
  padding: 0;
}

.navigation--items ul li {
  margin-bottom: 20px;
}

.navigation--items ul li a {
  color: #757575;
  font-size: 21px;
